INSERT INTO `ps_product_lang` (`id_product`, `id_shop`, `id_lang`, `description`, `description_short`, `link_rewrite`, `meta_description`, `meta_keywords`, `meta_title`, `name`, `available_now`, `available_later`, `delivery_in_stock`, `delivery_out_stock`) VALUES
(5154, 1, 1, 'XX a. Lietuvos valstybei likimas lėmė du kartus atgimti. Pažvelgus į Kauno technologijos universiteto istoriją, galima pamatyti, kad ir Universitetas ne kartą buvo priverstas keistis. Pokyčius lėmė ne tik pasaulio mokslo ir technikos pažanga, bet ir istorijos bei politikos verpetai, karai ir okupacijos.  \r\nMonografijoje nagrinėjama Universiteto istorija – nuo ištakų iki šių dienų. Knygos autorė analizuoja Lietuvos aukštojo mokslo raidą ir ypatumus Kaune pirmosios Lietuvos Respublikos laikotarpiu, Universiteto veiklą sudėtingu sovietų ir nacių okupacijų laikotarpiu, tuometinio Kauno politechnikos instituto raidą, universitetinio statuso atgavimą ir Universiteto raidos tendencijas laisvoje Lietuvoje. \r\nVieno didžiausių universitetų Lietuvoje – Kauno technologijos universiteto istorija menkai žinoma. Todėl monografija bus ypač aktuali tyrinėjantiems Lietuvos mokslo bei švietimo istoriją, padės atkurti istorinį teisingumą ir atgaivinti istorinę atmintį.\r\n', '', 'kauno-technologijos-universiteto-istorijos-kelias', '', '', '', 'Kauno technologijos universiteto istorijos kelias     ', '', '', '', ''),
(5154, 1, 2, '', '', '', '', '', '', '', '', '', '', ''),
(5155, 1, 1, 'Leidinyje aprašomas mechanikos mokslo kelias nuo Lietuvos universiteto ištakų iki dabartinio Kauno technologijos universiteto gyvenimo. Jame atspindimos iškilusios Universiteto asmenybės: jų veikla prieškariu, inžinierinė kūryba Kauno politechnikos institute ir dabar Kauno technologijos universitete. Išryškinami įspūdžiai ir remiantis mechaniškųjų fakultetų analize ieškoma mokslo pažangos.  ', '', 'mechanikos-mokslo-ikvepti-lietuvos-universitetas-kauno-technologijos-universitetas', '', '', '', 'Mechanikos mokslo įkvėpti. Lietuvos universitetas–Kauno technologijos universitetas', '', '', '', ''),
(5155, 1, 2, '', '', '', '', '', '', '', '', '', '', ''),
(5156, 1, 1, 'Šiame leidinyje analizuojamos socialinės, aplinkosauginės, ekonominės, technologinės bei geopolitinės rizikos Lietuvoje, ekspertinis jų vertinimas ir visuomenės rizikos suvokimas.\r\nAutorės pristato objektyvius rizikos indikatorius (duomenis iš įvairių oficialių institucijų), pateikia ekspertinius vertinimus (Lietuvos savivaldybių ekspertų internetinė apklausa) ir analizuoja gyventojų rizikos suvokimą (reprezentatyvi Lietuvos gyventojų apklausa).\r\nPagal atliktus tyrimus, autorės suformuluoja reikšmingas rizikų valdymo rekomendacijas, kurios adresuojamos savivaldybėms, ekonominės, socialinės, technologijų plėtros ir saugos, aplinkos, nacionalinio saugumo bei civilinės saugos politikų formuotojams ir įgyvendintojams.\r\n', '', 'rizikos-zemelapiai-lietuvoje-trumpoji-tyrimo-rezultatu-ataskaita-ir-rekomendacijos-politikos-formuotojams-bei-savivaldybems', '', '', '', 'Rizikos žemėlapiai Lietuvoje. Trumpoji tyrimo rezultatų ataskaita ir rekomendacijos politikos formuotojams bei savivaldybėms', '', '', '', ''),
(5156, 1, 2, '', '', '', '', '', '', '', '', '', '', ''),
(5157, 1, 1, 'Šis leidinys skirtas savarankiškoms studijoms ir kaip pagalbinė bendrosios chemijos laboratorinių darbų priemonė. Knygoje pateiktas trumpas dvylikos temų teorijos kursas ir užduotys su sprendimo pavyzdžiais, kurie padės geriau įsisavinti žinias.\r\nMokomoji knyga skiriama bakalauro studijų programos studentams, studijuojantiems technologinius mokslus. Ji pritaikyta moduliui „Bendroji chemija“, tačiau ja gali naudotis ir kitus chemijos dalyko modulius studijuojantys pirmojo ir aukštesniųjų kursų studentai. \r\n\r\n', '', 'bendroji-chemija-teorija-ir-individualiosios-uzduotys', '', '', '', 'Bendroji chemija. Teorija ir individualiosios užduotys', '', '', '', ''),
(5157, 1, 2, '', '', '', '', '', '', '', '', '', '', ''),
