INSERT INTO `ps_product_lang` (`id_product`, `id_shop`, `id_lang`, `description`, `description_short`, `link_rewrite`, `meta_description`, `meta_keywords`, `meta_title`, `name`, `available_now`, `available_later`, `delivery_in_stock`, `delivery_out_stock`) VALUES
(5295, 1, 1, 'Monografijoje pateikiama pirmoji lietuviška originali emocinio intelekto vertinimo metodika EI-DARL, apibūdinamas šalies jaunimo emocinio intelekto turinys ir jo raiška, atskleidžiami emocinį intelektą veikiantys psichosocialiniai ir demografiniai faktoriai, pristatomas įvairiuose Lietuvos regionuose vykęs jaunimo emocinio intelekto tyrimas. Autorės ieško atsakymų į klausimus, padėsiančius giliau suprasti Lietuvos jaunimo emocinio intelekto psichologinės genezės ir raidos mechanizmą. Leidinys skirtas psichologijos, sociologijos, edukologijos mokslų specialistams ir visiems, besidomintiems šia sritimi.', '', 'emocinis-intelektas-lietuvos-jaunimo-tyrimas', '', '', '', 'Emocinis intelektas: Lietuvos jaunimo tyrimas', '', '', '', ''),
(5295, 1, 2, '', '', '', '', '', '', '', '', '', '', ''),
(5296, 1, 1, 'Sveikata 4.0 revoliucijos kontekste kuriamos ir diegiamos pažangiausios sveikatos priežiūros technologijos, o pasaulinė pandemija dar labiau išryškino virtualaus bendravimo ir žinių dalinimosi socialiniuose tinkluose reikšmę tiek pacientams, tiek sveikatos priežiūros sistemoms. Šių dviejų transformuojančių pokyčių sandūroje fizinį sveikatos priežiūros specialistų ir pacientų bendravimą kaip pagrindinę lėtinių ligų stebėjimo ir valdymo formą keičia sąveika per skaitmenines sveikatos technologijas. Įvairiais tyrimais pagrįsta, kad socialiniai santykiai sukuria teigiamą efektą, ypač vykdant lėtinių ligų prevenciją ir gydant jas.\r\nNumatoma, kad artimiausius penkerius metus sveikatos priežiūros modelis dar toliau nukryps nuo čia ir dabar sveikatos priežiūros valdymo ir koncentruosis į tęstinį sveikatos priežiūros procesą, pagrįstą technologijomis. Fizinį sveikatos priežiūros specialistų ir pacientų bendravimą pakeis skaitmeninės sveikatos technologijos – pacientų bendravimas naudojant programėlę ir pacientų stebėjimas bei diagnostika nuotoliniu būdu.\r\nInternetinių lėtinėmis ligomis sergančių pacientų bendruomenių reikšmė ta, kad palaikydamos socialinius santykius jos suteikia prieinamą būdą dalintis turimomis žiniomis ir taip kurti naujas žinias. Tokiu būdu žinios „auginamos“ ir didina pacientų galimybes adekvačiai savipriežiūrai kasdieniame gyvenime.\r\nKas būdinga pacientų įgalinimui? Kokie yra pacientų žinių įgalintojai? Kokie yra lėtinėmis ligomis sergančių pacientų vaidmenys ir jų žinių dalinimosi elgsenos bruožai internetinėse sveikatos bendruomenėse? Kokia tikėtina skaitmeninių sveikatos technologijų ateitis ir kokie iššūkiai kyla suinteresuotiems skaitmenine sveikata? Šie ir daugelis kitų klausimų aptariami dr. Vestinos Vainauskienės ir dr. Rimgailės Vaitkienės parengtoje mokslo monografijoje „Pacientų žinių įgalinimas internetinėse sveikatos bendruomenėse“.\r\nAutorių mokslinės įžvalgos bus vertingos vadybos, visuomenės sveikatos specialistams, sveikatos politikos formuotojams, sveikatos technologijas kuriantiems ir vystantiems startuoliams. Monografija kaip mokslinių žinių šaltinis taip pat skirta vadybos ir visuomenės sveikatos magistrantūros ir doktorantūros studijoms.', '', 'pacientu-ziniu-igalinimas-internetinese-sveikatos-bendruomenese', '', '', '', 'Pacientų žinių įgalinimas internetinėse sveikatos bendruomenėse', '', '', '', ''),
(5296, 1, 2, '', '', '', '', '', '', '', '', '', '', ''),
